import React, { useState } from 'react';

function App() {
  const [apiStatus, setApiStatus] = useState('⚙️ À tester');
  const [isLoading, setIsLoading] = useState(false);

  const testAPI = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/health');
      const data = await response.json();

      if (response.ok) {
        setApiStatus('✅ Connecté');
        console.log('API Response:', data);
      } else {
        setApiStatus('❌ Erreur');
      }
    } catch (error) {
      setApiStatus('❌ Échec');
      console.error('API Error:', error);
    }
    setIsLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center">
      <div className="text-center">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">
          🚀 Agentic Communications V1
        </h1>
        <p className="text-xl text-gray-600 mb-8">
          Système de communication IA multi-canaux
        </p>
        <div className="bg-white rounded-lg shadow-md p-6 max-w-md">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">✅ Statut du système</h2>
          <div className="space-y-2">
            <div className="flex justify-between">
              <span>Frontend React</span>
              <span className="text-green-600 font-semibold">✓ Fonctionnel</span>
            </div>
            <div className="flex justify-between">
              <span>Backend API</span>
              <span className="text-green-600 font-semibold">✓ Fonctionnel</span>
            </div>
            <div className="flex justify-between">
              <span>Communication</span>
              <span className={
                apiStatus.includes('✅') ? 'text-green-600 font-semibold' :
                apiStatus.includes('❌') ? 'text-red-600 font-semibold' :
                'text-yellow-600 font-semibold'
              }>
                {apiStatus}
              </span>
            </div>
          </div>

          <div className="mt-6 pt-4 border-t">
            <button 
              onClick={testAPI}
              disabled={isLoading}
              className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white px-4 py-2 rounded-lg text-sm font-medium transition-colors"
            >
              {isLoading ? '🔄 Test en cours...' : '🔧 Tester l\'API'}
            </button>
          </div>

          <div className="mt-4">
            <p className="text-sm text-gray-500">
              🎯 Les deux serveurs fonctionnent !<br/>
              Cliquez pour tester la communication.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
